let createBtn=document.querySelector(".btn--create").addEventListener("click",e=>{let t=document.querySelector("#LobbyName").value,a=document.querySelector("#PeopleAmount").value;fetch("/api/v1/lobby",{method:"post",headers:{Accept:"application/json, text/plain, */*","Content-Type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")},body:JSON.stringify({lobbyname:t,owner:localStorage.getItem("username"),playersamount:parseInt(a),playersinside:[{user_id:localStorage.getItem("user_id"),username:localStorage.getItem("username"),gamesplayed:localStorage.getItem("gamesplayed"),gameswon:localStorage.getItem("gameswon")}]})}).then(e=>e.json()).then(e=>{if("success"==e.status){console.log(e.data.lobby._id);let t=e.data.lobby._id;window.location.href=`/lobby/${t}`}}),e.preventDefault()});