let loginBtn=document.querySelector(".btn--login").addEventListener("click",e=>{let t=document.querySelector("#username").value,a=document.querySelector("#password").value;fetch("/users/login",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:t,password:a})}).then(e=>e.json()).then(e=>{if("success"===e.status){let t=e.data.token,a=e.data.user_id,o=e.data.username,s=e.data.gamesplayed,l=e.data.gameswon;localStorage.setItem("token",t),localStorage.setItem("user_id",a),localStorage.setItem("username",o),localStorage.setItem("gamesplayed",s),localStorage.setItem("gameswon",l),window.location.href="/"}else alert("login failed")}),e.preventDefault()});