let lobbiesContainer=document.querySelector(".btn--leave").addEventListener("click",e=>{let t=localStorage.getItem("user_id"),o=window.location.href.replace(/\/$/,""),n=o.substr(o.lastIndexOf("/")+1);fetch(`/api/v1/lobby/${n}/${t}`,{method:"put",headers:{Accept:"application/json, text/plain, */*","Content-Type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")}}).then(e=>e.json()).then(e=>{"success"==e.status&&(window.location.href="/")}),e.preventDefault()});