let SignUpBtn=document.querySelector(".btn--signup").addEventListener("click",e=>{let t=document.querySelector("#name").value,a=document.querySelector("#username").value,o=document.querySelector("#email").value,s=document.querySelector("#password").value;fetch("/users/signup",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:t,username:a,email:o,gamesplayed:0,gameswon:0,password:s})}).then(e=>e.json()).then(e=>{if("success"==e.status){let t=e.data.token,a=e.data.user_id,o=e.data.username,s=e.data.gamesplayed,l=e.data.gameswon;localStorage.setItem("token",t),localStorage.setItem("user_id",a),localStorage.setItem("username",o),localStorage.setItem("gamesplayed",s),localStorage.setItem("gameswon",l),window.location.href="/"}else alert("login failed")}),e.preventDefault()});