let SignUpBtn=document.querySelector(".btn--signup").addEventListener("click",e=>{let t=document.querySelector("#name").value,a=document.querySelector("#username").value,n=document.querySelector("#email").value,o=document.querySelector("#password").value;fetch("/users/signup",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:t,email:n,username:a,password:o})}).then(e=>e.json()).then(e=>{if("success"==e.status){let t=e.data.token,a=e.data.user_id,n=e.data.username;localStorage.setItem("token",t),localStorage.setItem("user_id",a),localStorage.setItem("username",n),window.location.href="/"}else alert("login failed")}),e.preventDefault()});