fetch("http://localhost:3000/api/v1/lobby",{method:"get",headers:{Accept:"application/json, text/plain, */*","Content-Type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")}}).then(n=>n.json()).then(n=>{if("success"==n.status)for(i=0;i<n.data.length;i++){let t=document.createElement("div");t.classList.add("content__list","content__list--home"),t.dataset.id=n.data[i]._id;let e=n.data[i].playersinside.length,s=`\n        <h2 class="content__list_name">${n.data[i].lobbyname}</h2>\n\n        <div class="content__list_amountPlayers">\n          <h5>\n            <span class="content__list_amountPlayers_amount">${e}</span>\n            /\n            <span class="content__list_amountPlayers_many">${n.data[i].playersamount}</span>\n            players inside\n          </h5>\n        </div>\n\n        <div class="content__list_playersInside">\n          <h6 class="content__list_playersInside_peopleTitle">People in lobby:</h6>\n          <div class="content__list_profilepic_wrapper--small">\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n          </div>\n        </div>\n\n        <div class="content__btn">\n          <a href="" data-id="${n.data[i]._id}" class="btn btn-success btn--join">Join</a>\n        </div>\n        `;t.innerHTML=s,document.querySelector(".content__wrapper--middle-middle").appendChild(t)}else if("failed"==n.status){let t=`\n      <h2 class="content__list_error">${n.message}</h2>\n      `;document.querySelector(".content__wrapper--bottom-middle").innerHTML=t}}).catch(n=>{console.log("error!!!"),console.log(n)});