fetch("http://localhost:3000/api/v1/lobby",{method:"get",headers:{Accept:"application/json, text/plain, */*","Content-Type":"application/json",Authorization:"Bearer "+localStorage.getItem("token")}}).then(n=>n.json()).then(n=>{if(console.log(n),"success"==n.status)for(i=0;i<n.data.length;i++){let t=document.createElement("div");t.classList.add("content__list","content__list--home"),t.dataset.id=n.data[i]._id;let s=`\n        <h2 class="content__list_name">${n.data[i].lobbyname}</h2>\n\n        <div class="content__list_amountPlayers">\n          <h5>\n            <span class="content__list_amountPlayers_amount">5</span>\n            /\n            <span class="content__list_amountPlayers_many">6</span>\n            players inside\n          </h5>\n        </div>\n\n        <div class="content__list_playersInside">\n          <h6 class="content__list_playersInside_peopleTitle">People in lobby:</h6>\n          <div class="content__list_profilepic_wrapper--small">\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n            <div class="content__list_profilepic--small"></div>\n          </div>\n        </div>\n\n        <div class="content__btn">\n          <button class="btn btn-success btn--join">Join</button>\n        </div>\n        `;t.innerHTML=s,document.querySelector(".content__wrapper--bottom-middle").appendChild(t)}}).catch(n=>{console.log("error!!!"),console.log(n)});